<script>
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);
export default {
  name: "about-section",
  mounted() {
    var containers = gsap.utils.toArray("#container");
    var titles = gsap.utils.toArray("#titles");

    var tl1 = gsap.timeline({
      scrollTrigger: {
        trigger: "#right-about",

        normalize: true,
      },
    });
    tl1
      .to("#right-about", {
        duration: 0.5,
        opacity: 1,
        scale: 1,
        ease: "power2.out()",
      })
      .to(titles, { opacity: 1 })
      .to(containers, { opacity: 1, stagger: 0.3 });

    var tl2 = gsap.timeline({
      scrollTrigger: {
        trigger: "#left-about",
        normalize: true,
      },
    });
    var leftContent = gsap.utils.toArray("#left-content");
    tl2
      .to("#left-about", {
        duration: 0.5,
        opacity: 1,
        scale: 1,
        ease: "power2.out()",
      })
      .to("#profile-pic", { scale: 1, duration: 0.5, ease: "back.out(1.7)" })
      .to(leftContent, { opacity: 1 });
  },
};
</script>
